mapper.update:

update 
	seckill
set 
sec_number =sec_number-1;
where seckill_id = #{seckillId}
and start_time <= #{killTime}
and end_time>=#{killTime}
and sec_number>0;

mapper.select:
select seckill_id ,sec_name,sec_number,start_time,end_time,create_time
from seckill
where seckill_id=#{seckillId};

select seckill_id ,sec_name,sec_number,start_time,end_time,create_time
from seckill
where rownum>#{offset}
and rownum<=#{limit}
order by create_time desc;
